

module.exports = Argument;

function Argument (arg, description, def) {

	var results = regex.exec(arg);

	this.arg = arg;
	this.name = results[1];
	this.optional = results[2] === '?';
	this.def = def;
	this.description = description;
	this.nonValue = arg.indexOf('<') < 0;

}

Argument.prototype.arguments = function (arg) {

	this.args.push(new Argument(arg));

	return this;

}

Argument.prototype.toString = function () {

	return this.arg;

}

var regex = exports.regex = new RegExp('<?([^>?]+)(\\?)?>?');